	);
	
	// 3000文字 + over
	public $length_over = '';
	
	
	/**
	* Curl
	*/
	public function use_curl($url, $post_data_arr)
	{
		
		$ch = curl_init();
		
		curl_setopt($ch, CURLOPT_URL, $url); 
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
		curl_setopt($ch, CURLOPT_HTTPHEADER, $this->headers_arr);
		curl_setopt($ch, CURLOPT_USERAGENT, 'test');
		
		curl_setopt($ch,CURLOPT_POST, true);
		curl_setopt($ch, CURLOPT_POSTFIELDS, $post_data_arr);
		//curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($post_data_arr));
		
		$result = curl_exec($ch);
		curl_close($ch);
		
		return $result;
		
	}
	
}